class PostComments{constructor(e){this.postId=e,this.postContainer=$(`#post-${e}`),this.newCommentForm=$(`#post-${e}-comments-form`),this.createComment(e);let t=this;$(" .delete-comment-button",this.postContainer).each((function(){t.deleteComment($(this))}))}createComment(e){let t=this;this.newCommentForm.submit((function(n){n.preventDefault();$.ajax({type:"post",url:"/comments/create",data:$(this).serialize(),success:function(n){let o=t.newCommentDom(n.data.comment);$("#comment-data").val(""),$(`#post-comments-${e}`).prepend(o),t.deleteComment($(" .delete-comment-button",o)),likeToggle($(" .newComment"),o),new Noty({theme:"relax",text:"Comment published!",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})})),$(".commentData").val("")}newCommentDom(e){return $(`<li id="comment-${e._id}">\n            <small id="comment-delete-button">\n                    <a  class="delete-comment-button" href="/comments/destroy/${e._id}"><i class="fa-solid fa-trash"></i></a>\n            </small>\n            <a id="post-user" href="/users/profile/${e.user._id}">\n                ${e.user.name}\n            </a>\n            <small id="like-button">\n                <a class="toggle-like-button newComment" data-likes="0" href="/likes/toggle/?id=${e._id}&type=Comment">\n                    0 Likes\n                </a>\n            </small>\n            <br>\n            <div id="comment-content">\n                ${e.content}\n            </div>\n        </li>`)}deleteComment(e){$(e).click((function(t){t.preventDefault(),$.ajax({type:"get",url:$(e).prop("href"),success:function(e){$(`#comment-${e.data.comment_id}`).remove(),new Noty({theme:"relax",text:"Comment Deleted",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})}))}}let likeToggle=function(e){new ToggleLike(e);e.removeClass("newPost")};